import React from "react";
import styles from "./styles.module.css";
class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "", invalid: true, valid: false };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    if (/[^a-zA-Z]/.test(this.state.value)) {
      this.setState({ invalid: false });
    } else {
      this.setState({ invalid: true });
      this.setState({ valid: true });
    }
    event.preventDefault();
  }

  render() {
    return (
      <div>
        {!this.state.valid ? (
          <form
            onSubmit={this.handleSubmit}
            style={{ width: 500, height: 650, backgroundColor: "#58B19F", justifyContent: "center" }}
          >
            <label>
              Name: 
              <input
                type="text"
                value={this.state.value}
                onChange={this.handleChange}
                style={{backgroundColor: "transparent",border:"solid white 1.3px",padding:2,margin:5,borderRadius:20}}
              />
            </label>
            <input type="submit" value="Submit" style={{ width: 100, height:40, padding: 2,flex:1,flexDirection:"row",textAlign:"center",color:"white",backgroundColor:"transparent",margin:10,borderRadius: 30,border:"solid snow .5"
          }} />
            {!this.state.invalid ? (
              <div style={{ color: "#0984e3" }}>
               Whoops sorry! please try again :)
              </div>
            ) : null}
          </form>
        ) : (
          <span style={{ width: 500, height: 650, backgroundColor: "#58B19F", justifyContent: "center",color: "white",textAlign:"center",border:"none"}}> Hello User! Your Name is {this.state.value}.</span>
        )}
      </div>
    );
  }
}
export default NameForm;
